Un exemple de base de PyGame pour la gestion des événements du clavier et l'affichage du compteur FPS pourrait ressembler à ceci:

import pygame

class Game:
    def __init__(self):
        # Initialisation de PyGame
        pygame.init()
        self.screen = pygame.display.set_mode((800, 600))
        self.clock = pygame.time.Clock()
        self.fps = 60
        self.is_running = True

    def event_loop(self):
        for event in pygame.event.get():
            if event.type == pygame.QUIT:
                self.is_running = False
            elif event.type == pygame.KEYDOWN:
                if event.key == pygame.K_ESCAPE:
                    self.is_running = False

    def render(self):
        # Effacer l'écran
        self.screen.fill((0, 0, 0))
        # Affichage du compteur FPS
        fps_text = f"FPS: {int(self.clock.get_fps())}"
        font = pygame.font.SysFont("Arial", 16)
        text_surface = font.render(fps_text, True, (255, 255, 255))
        self.screen.blit(text_surface, (10, 10))
        # Mettre à jour l'écran
        pygame.display.flip()

    def run(self):
        while self.is_running:
            self.event_loop()
            self.render()
            self.clock.tick(self.fps)

if __name__ == "__main__":
    game = Game()
    game.run()

