Voici un exemple de projet de base en PyGame qui gère les événements du clavier et affiche le compteur FPS dans le coin supérieur gauche de la fenêtre:

import pygame

class Game:
    def __init__(self):
        pygame.init()
        self.screen = pygame.display.set_mode((800, 600))
        self.clock = pygame.time.Clock()
        self.fps = 60
        self.running = True

    def run(self):
        while self.running:
            for event in pygame.event.get():
                if event.type == pygame.QUIT:
                    self.running = False
                elif event.type == pygame.KEYDOWN:
                    if event.key == pygame.K_ESCAPE:
                        self.running = False

            self.screen.fill((0, 0, 0))
            fps_text = f"FPS: {int(self.clock.get_fps())}"
            text_surface = pygame.font.SysFont("Arial", 24).render(fps_text, True, (255, 255, 255))
            self.screen.blit(text_surface, (10, 10))

            pygame.display.flip()
            self.clock.tick(self.fps)

game = Game()
game.run()

