#include <iostream>
using namespace std;

int fibonacci(int n) {
    int dp[n+1];
    dp[0] = 0;
    dp[1] = 1;

    for(int i=2; i<=n; i++) {
        dp[i] = dp[i-1] + dp[i-2];
    }

    return dp[n];
}

int main() {
    int n;
    cout << "Enter the value of n: ";
    cin >> n;

    cout << "The nth Fibonacci number is: " << fibonacci(n);
    return 0;
}

