            // प्रत्येक पंक्ति में शब्दों की संख्या जाँचें 
            count += count(word, word);
        }
        file.close();
    } else {
        cout << "Error opening file";
    }

    cout << "Word appears " << count << " times." << endl;

    return 0;
}

int count(string s1, string s2) {
    int count = 0;
    size_t pos = 0;

    while ((pos = s1.find(s2, pos)) != string::npos) {
        count++;
        pos += s2.length();
    }

    return count;
}
```

