int count_occurrences(string text, string word) {
    int count = 0;
    int word_length = word.length();

    for (int i = 0; i < text.length(); i++) {
        if (text.substr(i, word_length) == word) {
            count++;
            i += word_length - 1;
        }
    }

    return count;
}

int main() {
