#include <vector>

std::vector<int> getCommonElements(std::vector<int> arr1, std::vector<int> arr2) {
  std::vector<int> commonElements;

  // merge two arrays and remove duplicates
  std::vector<int> mergedArr = arr1;
  for (int element : arr2) {
    if (std::find(mergedArr.begin(), mergedArr.end(), element) == mergedArr.end()) {
      mergedArr.push_back(element);
    }
  }

  // get common elements
  for (int element : mergedArr) {
    if (std::count(arr1.begin(), arr1.end(), element) > 0 && std::count(arr2.begin(), arr2.end(), element) > 0) {
      commonElements.push_back(element);
    }
  }

  return commonElements;
}

int main() {
  std::vector<int> arr1 = {1, 2, 3, 4, 5};
  std::vector<int> arr2 = {4, 5, 6, 7, 8};

  std::vector<int> commonElements = getCommonElements(arr1, arr2);

  for (int element : commonElements) {
    std::cout << element << " ";
  }

  return 0;
}
```

