#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
  string word; // Ordet vi ser etter
  int count = 0; // Antall forekomster av ordet

  cout << "Skriv inn ordet du vil finne forekomster av: ";
  cin >> word;

  // Åpner filen
  ifstream input("filnavn.txt");

  // Leker linje for linje
  string line;
  while (getline(input, line)) {
    size_t pos = 0;
    while ((pos = line.find(word, pos)) != string::npos) {
      count++;
      pos += word.length();
    }
  }

  cout << "Antall forekomster av " << word << " er: " << count << endl;

  return 0;
}
```

