int main() {
    string wordToFind;
    cout << "Enter the word you want to search for: ";
    getline(cin, wordToFind);

    int count = 0;
    string line;
    ifstream infile("myfile.txt");
    while (getline(infile, line)) {
        size_t pos = line.find(wordToFind);
        while (pos != string::npos) {
            count++;
            pos = line.find(wordToFind, pos + 1);
        }
    }
