#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
  string word; // Ordet som ska räknas
  int count = 0; // Antal förekomster av ordet

  cout << "Ange ordet du vill söka efter: ";
  getline(cin, word);

  ifstream file("textfil.txt"); // Öppna textfilen

  while (file >> word) {
    if (word == word) {
      count++; // Räkna upp antalet förekomster
    }
  }

  cout << "Antalet förekomster av " << word << " är " << count << endl;

  return 0;
}
