```cpp
#include <iostream>
#include <fstream>
#include <string>

int main(int argc, char* argv[]) {
  if (argc < 3) {
    std::cerr << "Usage: " << argv[0] << " <input file> <word to count>" << std::endl;
    return -1;
  }

  std::ifstream file(argv[1]);
  std::string word(argv[2]);
  int count = 0;

  if (!file.is_open()) {
    std::cerr << "Error opening file: " << argv[1] << std::endl;
    return -1;
  }

  while (file.good()) {
    std::string line;
    std::getline(file, line);
    if (!line.empty() && line.find(word) != std::string::npos) {
      count++;
    }
  }

  std::cout << "Count: " << count << std::endl;

  return 0;
}
```

