#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    // Öffnen der Textdatei
    ifstream infile("myfile.txt");

    // Lesen der ersten Zeile
    string line;
    getline(infile, line);

    // Variablen für die Anzahl der Vorkommnisse
    int wordCount = 0;

    // Schleife für jede Zeile in der Datei
    while (infile) {
        // Lesen der aktuellen Zeile
        getline(infile, line);

        // Prüfen, ob das Wort in der aktuellen Zeile vorkommt
        size_t pos = line.find("Wort");
        if (pos != string::npos) {
            wordCount++;
        }
    }

    // Ausgabe des Ergebnisses
    cout << "Das Wort 'Wort' kommt " << wordCount << " Mal in der Datei vor." << endl;

    return 0;
