using System;

class Fibonacci
{
    public static long Fibonacci(long n)
    {
        if (n <= 1)
        {
            return n;
        }

        long[] memo = new long[n + 1];

        for (int i = 0; i < n + 1; i++)
        {
            memo[i] = -1;
        }

        memo[0] = 0;
        memo[1] = 1;

        for (int i = 2; i <= n; i++)
        {
            if (memo[i] == -1)
            {
                memo[i] = memo[i - 1] + memo[i - 2];
            }
        }

        return memo[n];
    }

    static void Main(string[] args)
    {
        Console.WriteLine(Fibonacci(10));
    }
}
