#include <iostream>
using namespace std;

int main() {
    int arr1[] = {1, 2, 3, 4, 5};
    int arr2[] = {3, 4, 5, 6, 7};

    bool found = false;
    for (int i = 0; i < sizeof(arr1) / sizeof(int); i++) {
        for (int j = 0; j < sizeof(arr2) / sizeof(int); j++) {
            if (arr1[i] == arr2[j]) {
                cout << "Common element found: " << arr1[i] << endl;
                found = true;
                break;
            }
        }
        if (found) {
            break;
        }
    }

    if (!found) {
        cout << "No common elements found." << endl;
    }

    return 0;
}
```

