def fib(n):
    if n <= 1:
        return n

    return fib(n - 1) + fib(n - 2)

def fib_loop(n):
    if n <= 1:
        return n

    a, b = 0, 1
    for _ in range(1, n):
        a, b = b, a + b

    return b

if __name__ == '__main__':
    n = 10
    print(f'fib({n}): {fib(n)}')
    print(f'fib_loop({n}): {fib_loop(n)}')
