#include <iostream>
#include <fstream>
#include <string>

int main() {
  std::string search_term;
  std::cout << "Anna etsittävä sana: ";
  std::cin >> search_term;

  std::ifstream file("tiedosto.txt");

  if (!file.is_open()) {
    std::cerr << "Tiedostoa ei löydy" << std::endl;
    return 1;
  }

  int count = 0;

  while (file.good()) {
    std::string line;
    std::getline(file, line);

    size_t pos = line.find(search_term);

    while (pos != std::string::npos) {
      count++;
      pos = line.find(search_term, pos + search_term.length());
    }
  }

  file.close();

  std::cout << "Sanaa " << search_term << " esiintyy tiedostossa " << count
            << " kertaa." << std::endl;

  return 0;
}
