```c++
#include <iostream>
using namespace std;

// dp[i]는 i번째 피보나치 수를 저장하는 배열
int dp[1001];

// 피보나치 수 계산 함수
int fib(int n) {
    // 종료 조건
    if (n <= 1) return n;

    // 이미 계산한 값이면 저장된 값 반환
    if (dp[n] != -1) return dp[n];

    // 아직 계산하지 않은 값이면 결과를 계산하고 저장
    dp[n] = fib(n - 1) + fib(n - 2);
    return dp[n];
}

int main() {
    int n;
    cin >> n;

    // 초기화: -1로 초기화하여 미계산된 값을 표시
    for (int i = 0; i <= 1000; i++) {
        dp[i] = -1;
    }

    // 피보나치 수 계산
    cout << fib(n) << endl;

    return 0;
}
```

