#include <stdio.h>

int main(int argc, char* argv[]) {
    int n, m, i, j, k;

    if (argc != 3) {
        fprintf(stderr, "Usage: %s <n> <m>\n", argv[0]);
        return 1;
    }

    n = atoi(argv[1]);
    m = atoi(argv[2]);

    int A[n][m], B[n][m], C[n][m];

    for (i = 0; i < n; ++i) {
        for (j = 0; j < m; ++j) {
            A[i][j] = rand() % 10;
            B[i][j] = rand() % 10;
        }
    }

    // Find common elements in A and B
    for (i = 0; i < n; ++i) {
        for (j = 0; j < m; ++j) {
            if (A[i][j] == B[i][j]) {
                C[i][j] = A[i][j];
            } else {
                C[i][j] = -1;
            }
        }
    }

    // Print common elements in C
    for (i = 0; i < n; ++i) {
        for (j = 0; j < m; ++j) {
            if (C[i][j] != -1) {
                printf("%d ", C[i][j]);
            }
        }
        printf("\n");
    }

    return 0;
}
```

