#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string word; // слово, для которого нужно подсчитать количество вхождений
    std::cout << "Введите слово: ";
    std::cin >> word;

    std::ifstream file("text_file.txt"); // считываем содержимое файла
    std::string line;
    int count = 0; // счетчик вхождений слова

    while (std::getline(file, line)) {
        size_t pos = 0;
        while ((pos = line.find(word, pos)) != std::string::npos) {
            count++;
            pos += word.length();
        }
    }

    std::cout << "Количество вхождений слова " << word << " в файле: " << count << std::endl;

    return 0;
}

