#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word; // слово, которое нужно найти
    int count = 0; // счетчик вхождений слова

    cout << "Введите слово: ";
    cin >> word;

    ifstream file("text.txt"); // имя файла, который нужно открыть

    if (file.is_open()) {
        string line; // строка, которая будет считываться из файла

        while (getline(file, line)) {
            size_t pos = line.find(word); // находит позицию первого вхождения слова в строке
            while (pos != string::npos) {
                count++; // увеличивает счетчик вхождений на 1
                pos = line.find(word, pos + word.length()); // находит следующее вхождение слова в строке
            }
        }

        file.close(); // закрываем файл после чтения
    } else {
        cout << "Ошибка открытия файла.";
    }

    cout << "Количество вхождений слова '" << word << "' в файле: " << count;

    return 0;
}

