#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word; // Ord som ska sökas
    string input; // Input-sträng
    int count = 0; // Förekomst-tellare

    cout << "Skriv in det ord som ska sökas: ";
    getline(cin, word); // Läser in ordet som ska sökas

    ifstream file("path/to/file.txt"); // Öppnar filen
    if (file.is_open()) {
        while (getline(file, input)) {
            size_t pos = input.find(word);
            while (pos != string::npos) {
                count++;
                pos = input.find(word, pos + 1);
            }
        }
        file.close();
    } else {
        cout << "Kunde inte öppna filen." << endl;
        return 1;
    }

    cout << "Det ordet finns " << count << " gånger i filen." << endl;
    return 0;
}

