#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word; // Ord som ska sökas efter
    int count = 0; // Räknar antalet gånger ordet förekommer

    // Läs in namnet på textfilen
    cout << "Skriv in namnet på textfilen: ";
    string filename;
    cin >> filename;

    // Öppna filen för att läsa in den
    ifstream file(filename);

    // Läs in varje rad i filen
    string line;
    while (getline(file, line)) {
        // Söker efter ordet i varje rad
        size_t pos = line.find(word);
        while (pos != string::npos) {
            count++;
            pos = line.find(word, pos + 1);
        }
    }

    // Skriv ut antalet gånger ordet förekommer i filen
    cout << "Antalet gånger " << word << " förekommer i filen är: " << count << endl;

    return 0;
}

