int main() {
  string word; // 单词
  int word_count = 0; // 单词出现次数
  ifstream file("input.txt"); // 打开输入文件

  if (file.is_open()) {
    // 逐行读取文本文件
    while (getline(file, word)) {
      // 计算特定单词出现次数
      if (word == "特定单词") {
        word_count++;
      }
    }
    file.close();
  } else {
    cout << "Failed to open file." << endl;
    return -1;
  }

  cout << "特定单词出现次数：" << word_count << endl;

  return 0;
}
```
