#include <fstream>
#include <string>
#include <iostream>

int main() {
    std::ifstream file("example.txt"); // Заместете "example.txt" с името на файла, който желаете да прочетете
    if (!file.is_open()) {
        std::cout << "Unable to open file" << std::endl;
        return 1;
    }

    std::string word_to_find;
    std::cout << "Enter the word to find: ";
    std::cin >> word_to_find;

    int count = 0;
    std::string line;
    while (std::getline(file, line)) { // Прочитане на всеки ред от файла
        size_t pos = 0;
        while ((pos = line.find(word_to_find, pos)) != std::string::npos) { // Търсене на думата във всеки ред и броене на броя на срещанията ѝ във файла
            count++;
            pos++; // Оптимизация: Прескачане на вече прочетените символи за да се избегне повторно преброяване на същата дума в един и същ ред
        }
    }

    std::cout << "The word " << word_to_find << " appears " << count << " times in the file." << std::endl;
    return 0;
}
