#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string wordToCount;
    std::cout << "Introduzca la palabra que desea contar: ";
    std::cin >> wordToCount;

    std::ifstream file("texto.txt");
    if (!file.is_open()) {
        std::cout << "No se pudo abrir el archivo." << std::endl;
        return 1;
    }

    int count = 0;
    std::string line;
    while (std::getline(file, line)) {
        size_t pos = line.find(wordToCount);
        while (pos != std::string::npos) {
            count++;
            pos = line.find(wordToCount, pos + 1);
        }
    }
