#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int countWordOccurrences(const string& word, const string& filename) {
    ifstream file(filename);
    if (!file.is_open()) {
        cout << "Error opening file: " << filename << endl;
        return -1;
    }

    int count = 0;
    string line;

    while (getline(file, line)) {
        size_t pos = 0;
        while ((pos = line.find(word, pos)) != string::npos) {
            ++count;
            pos += word.length();
        }
    }

    file.close();
    return count;
}

