#include <iostream>
#include <fstream>
#include <string>
#include <unordered_map>

int main() {
    std::string wordToCount;
    std::cout << "Enter the word to count: ";
    std::cin >> wordToCount;

    std::unordered_map<std::string, int> wordCount;

    std::ifstream file("example.txt");

    if (file.is_open()) {
        std::string line;
        while (std::getline(file, line)) {
            std::stringstream ss(line);
            std::string word;
            while (ss >> word) {
                if (word == wordToCount) {
                    wordCount[word]++;
                }
            } 
        } 
        file.close(); 
    } else { 
        std::cerr << "Unable to open file" << std::endl; 
        return 1; 
    } 

    for (auto const& [word, count] : wordCount) { 
        std::cout << "The word \"" << word << "\" appears " << count << " times." << std::endl; 
    } 
