#include <iostream>
#include <fstream>
#include <string>

int count_word(const std::string& file_name, const std::string& word) {
    std::ifstream file(file_name);
    if (!file.is_open()) {
        std::cerr << "Failed to open file: " << file_name << std::endl;
        return 0;
    }

    int count = 0;
    std::string line;
    while (std::getline(file, line)) {
        size_t pos = 0;
        while ((pos = line.find(word, pos)) != std::string::npos) {
            ++count;
            ++pos;
        }
    }
    return count;
}
