            word_counts.push_back(counter); // Das Wort in die Liste einfügen.
            counter = 0; // Die Anzahl der Vorkommen zurücksetzen.
        }
    }
    for (int i = 0; i < word_counts.size(); i++) { // Die Anzahl der Vorkommen für jedes Wort ausgeben.
