    std::ifstream input_file("text_file.txt"); // Open the text file for reading
    if (!input_file) { // Check if the file was successfully opened
        std::cerr << "Error opening the file" << std::endl; // Print an error message and exit the program
        return -1; // Return a negative value to indicate failure
    }

    std::vector<std::string> words; // Create a vector to store the words in the text file
