        for (int i = 0; i < words.size(); i++) { // Loop through each word in the vector to count its occurrences
            int occurrences = 0; // Initialize a variable to count the number of occurrences
            for (int j = 0; j < words.size(); j++) { // Loop through each word in the vector again to count its occurrences
                if (words[j] == "specific_word") { // Check if the current word is "specific_word"
                    occurrences++; // Increment the number of occurrences if it is found
                }
            }
            words[i] += std::to_string(occurrences); // Append the number of occurrences to each word in the vector, separated by a space character (' ')
        }
        std::cout << words[0] << std::endl; // Print out each word in the vector, separated by newlines ('\n')
    }
    return 0; // Return 0 to indicate success
}

