def common_elements(array1, array2):
    # Initialize a set to store the common elements
    set1 = set(array1)
    set2 = set(array2)

    # Loop through both arrays and add the elements that are present in both arrays to the set
    for element in array1:
        if element in set2:
            set1.add(element)
    for element in array2:
        if element in set1:
            set2.add(element)

    # Return the size of the intersection between the two sets (the common elements)
    return len(set1.intersection(set2))
