def fib(n):
    """Returns the nth Fibonacci number."""
    if n == 0:
        return 0
    elif n == 1:
        return 1
    else:
        dp = [[0 for _ in range(n+1)] for _ in range(n+1)]
        for i in range(2, n+1):
            dp[i][0] = i
        for i in range(1, n+1):
