```javascript
// Verwenden Sie Array.prototype.reduce um die Elemente in den beiden Arrays zu verschachteln
const result = [...arr1, ...arr2].reduce((accumulator, currentValue) => {
  if (accumulator.includes(currentValue)) {
    return accumulator;
  } else {
    accumulator.push(currentValue);
    return accumulator;
  }
}, []);

// Verwenden Sie Array.prototype.filter um alle Elemente aus dem Ergebniss-Array zu entfernen, die sich in beiden Arrays gleichen
const filtered = result.filter((value) => {
  return arr1.includes(value) || arr2.includes(value);
});
