#include <iostream>
#include <fstream>
#include <string>
using namespace std;
int main() {
    ifstream myfile("palabras.txt");
    string palabra_buscada("hola");
    int contador = 0;
    string linea;
    while (getline(myfile, linea)) {
        if (linea.find(palabra_buscada) != string::npos) {
            contador++;
        }
    }
    cout << "La palabra " << palabra_buscada << " se repite " << contador << " veces." << endl; 
