from pygame import *
import time

running = True

screen = display.set_mode((600, 600))
clock = pygame.time.Clock()

contador = 0
fps = 0
while running:
    for event in pygame.event.get():
        if event.type == QUIT:
            running = False
        elif event.type == KEYDOWN:
            if event.key == K_ESCAPE:
                running = False
        elif event.type == KEYUP:
            if event.key == K_ESCAPE:
                running = False
        else:
            pass # ignore other events such as mouse movements or keyboard presses outside of the ESC and FPS printouts.

