using System;
using System.Linq;

namespace FibonacciTest
{
    class Program
    {
        static void Main(string[] args)
        {
            Console.WriteLine("Hitta det n:te Fibonacci-talet med hjälp av LINQ");
            Console.Write("Antal termer: ");
            int count = int.Parse(Console.ReadLine());
            Console.WriteLine("Den första termen är: 1");
            Console.WriteLine("Den andra termen är: 1");
            for (int i = 2; i < count; i++)
            {
                Console.WriteLine($"Den {i}:te termen är: {(int)Fibonacci(i)}");
            }
        }
        static int Fibonacci(int n) => n > 0 ? Fibonacci(n - 1) + Fibonacci(n - 2) : 0;
    }
}

