```cpp
#include <iostream>
#include <string>
#include <fstream>
#include <cstdlib>

using namespace std;

int main(int argc, char** argv) {
    if (argc != 2) {
        cerr << "Ungültige Anzahl von Argumenten. " << endl;
        return EXIT_FAILURE;
    }
    ifstream file(argv[1]);
    if (!file) {
        cerr << "Fehler beim Lesen der Datei: " << argv[1] << endl;
        return EXIT_FAILURE;
    }
    int count = 0; // Anzahl der Vorkommnisse des Wortes in der Datei.
    string word = argv[1]; // Wort, das gesucht wird.
    string line; // Zeile aus der Datei.
    while (getline(file, line)) { // Lesen einer Zeile aus der Datei.
        if (line.find(word) != string::npos) { // Wenn das Wort in der Zeile enthalten ist, zählen wir es auf.
            count++; // Anzahl erhöhen.
        } else { // Sonst ist es eine neue Zeile, die wir durchlaufen müssen.
            count = 0; // Anzahl auf null setzen. 
        }
    }// Ende while (getline(file, line)) { Lesen einer Zeile aus der Datei.  
    cerr << "Vorkommen des Wortes \"" << word << "\" in der Datei: " << argv[1] << ": " << count << endl; // Ausgabe des Ergebnisses.  
}// Ende main(int argc, char** argv) {  
}// Ende Programm  
