import pygame
from pygame.locals import *

pygame.init()
screen = pygame.display.set_mode((640, 480))
clock = pygame.time.Clock()

running = True
while running:
    for event in pygame.event.get():
        if event.type == QUIT:
            running = False
        elif event.type == KEYDOWN:
            if event.key == K_ESCAPE:
                running = False
        elif event.type == KEYUP:
            if event.key == K_ESCAPE:
                running = False
    screen.fill(0)
    clock.tick(60)
    pygame.display.flip()
pygame.quit()

