```c++
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    ifstream fin("datei.txt");
    string word = "word"; // das Wort, das gesucht werden soll
    int counter = 0;      // die Anzahl der Vorkommnisse des Wortes
    while (fin >> word) { // lesen eines Wortes aus der Datei
        if (word == word) { // das Wort ist gelesen, es gibt also Vorkommen
            ++counter;     // zählen der Anzahl der Vorkommnisse des Wortes
        }
    }
    fin.close();             // die Datei schließen
    cout << "Anzahl der Vorkommnisse: " << counter << endl; // die Anzahl anzeigen
}
