```c++
// C++ Program to read a paragraph and find the occurrence of a specific word.
// Read the paragraph line by line and extract words from each line.
#include <iostream>
#include <string>
using namespace std;

int main() {
    string line, word;
    int word_counter = 0; // Count of the word occurrence.
    // Read the paragraph line by line.
    ifstream input("file.txt");
    while (getline(input, line)) { // Get lines from file.txt one by one.
        for (int i = 0; i < line.size(); i++) { // Extract words from each line.
            word += line[i]; // Add current character to the string 'word'.
            if (word == "word") { // If current character is equal to the target word, increment the counter.
                word_counter++; // Increment the counter of the word occurrence.
            } else { // Else break from the loop to avoid duplicate counting of target word.
                break;
            }
        }
    }
    cout << "Word found " << word_counter << " times." << endl; // Print the result in console.
    return 0; // Successful termination of program.
}
