    // Create an array of all the Roblox Luau characters
    const characters = ["Fisherman", "Surfer", "Gypsy", "Tropical", "Hula", "DJ", "Bartender", "Male_Model", "Coconut_Sheller", "Pirate", "Tropical_Dude", "Tropical_Girl"]

