def find_common_elements(matrix1, matrix2):
    common_elements = set()
    for row in range(matrix1.shape[0]):
        for col in range(matrix1.shape[1]):
            if matrix1[row][col] == matrix2[row][col]:
                common_elements.add((row, col))
    return common_elements

