```
#include <iostream>
using namespace std;

int main() {
    int n; // Tạo một biến để lưu giá trị cho số Fibonacci thứ n
    cout << "Nhập giá trị của số Fibonacci thứ n: ";
    cin >> n; // Nhập giá trị cho số Fibonacci thứ n
    int a = 0, b = 1; // Lập giá trị cho hai số Fibonacci đầu tiên
