        if (line.find("word") != string::npos) { // If the current line contains the specified word, increment the word count variable
            wordCount++;
        }
    }
    cout << "The word " << "word" << " appears " << wordCount << " times in the input file."; // Print a message to standard output with the specified word and its occurrence count
}
