contract MainContract {
  address owner;
  mapping (address => uint256) public tokens;
  mapping (address => bool) public isInitialized;

  function initializeTokens(uint256[] memory tokens) public {
    owner = msg.sender;
    tokens[msg.sender] = tokens[msg.sender] + tokens[msg.sender];
  }

  function sellTokens(uint256 tokenId, uint256 amount) public payable {
    if(!isInitialized(tokenId)) throw;
    isInitialized(tokenId) = false;

    tokens[tokenId] -= amount;
  }

  function getTokenBalance(uint256 tokenId) public view returns (uint256) {
    if(!isInitialized(tokenId)) throw;
    return tokens[tokenId];
  }

  function updateIsInitialized() public payable {
    isInitialized = true;
  }
}

contract TokenContract1 {
  address tokenOwner;
  uint256 tokenId;
  mapping(address => uint256) public balanceOfToken;

  function setTokenOwner(address newOwner) public {
    tokenOwner = newOwner;
  }

  function setTokenId(uint256 newTokenId) public {
    tokenId = newTokenId;
  }

