    screen.fill(pygame.Color("black"))
    pygame.display.update()

    # Check for keyboard events
    if key[pygame.K_ESCAPE]:
        running = False
        running_event = True

    # Check for mouse events
    if key[pygame.K_LEFT]:
        screen.blit(pygame.transform.rotate(pygame.transform.reflect(pygame.transform.rotate(screen, -90), (0, 1, 0)), -90), screen.get_rect())
    elif key[pygame.K_RIGHT]:
        screen.blit(pygame.transform.rotate(pygame.transform.reflect(pygame.transform.rotate(screen, 90), (0, 1, 0)), 90), screen.get_rect())
    elif key[pygame.K_DOWN]:
        screen.blit(pygame.transform.rotate(pygame.transform.reflect(pygame.transform.rotate(screen, -90), (1, 0, 0)), -90), screen.get_rect())
    elif key[pygame.K_UP]:
        screen.blit(pygame.transform.rotate(pygame.transform.reflect(pygame.transform.rotate(screen, 90), (-1, 0, 0)), 90), screen.get_rect())

    # Print FPS
    if running_event:
        print("FPS:", round((1000/time since last update)/5,2))

    pygame.display.update()
