void convertirTextoMarkdownAHTML(std::string textoMarkdown, std::string textoHTML) {
    std::string textoHTMLFormateado = textoHTML;

    for (int i = 0; i < textoMarkdown.size(); i++) {
        if (textoMarkdown[i] == '%') {
            switch (textoMarkdown[i + 1]) {
            case 'a':
                textoHTMLFormateado.append("<a>");
                break;
            case 'b':
                textoHTMLFormateado.append("<b>");
                break;
            case 'i':
                textoHTMLFormateado.append("<i>");
                break;
            case 's':
                textoHTMLFormateado.append("<s>");
                break;
            case 'u':
                textoHTMLFormateado.append("<u>");
                break;
            case 'strong':
                textoHTMLFormateado.append("<strong>");
                break;
            case 'em':
                textoHTMLFormateado.append("<em>");
                break;
            default:
                textoHTMLFormateado.append(textoMarkdown[i]);
            }
        }
    }

    return textoHTMLFormateado;
}

