def find_fibo(n):
    a = [1] * n
    for x in range(1, n + 1):
        if x == 0:
            continue
        if x == n:
            a.remove(x)
        else:
            a.append(x)
            for i in range(len(a) - 1):
                if a[i] == a[i + 1]:
                    a.pop()
                    break
            continue

    return a[n]
