#include <iostream>
#include <string>
using namespace std;

std::string formatMarkdownToHTML(const std::string& markdown)
{
    std::string html;
    for (const char* c = markdown.c_str(); *c; c++)
    {
        if (*c == '\n')
        {
            html += "<br/>";
        }
        else if (*c == '\r')
        {
            html += "<br/>";
        }
        else if (*c == '\t')
        {
            html += "&nbsp;";
        }
        else
        {
            html.push_back(*c);
        }
    }

    return html;
}

int main()
{
