class WarteschlangenQueue:
    def __init__(self):
        self.liste = []
        self.index = 0

    def enqueue(self, element):
        self.liste.append(element)
        self.index = self.index + 1

    def dequeue(self):
        if not self.liste:
            raise IndexError("WarteschlangenQueue is empty.")

        return self.liste[self.index]

    def isEmpty(self):
        return self.index == len(self.liste)

    def isFull(self):
